'use strict';

var debug = require('debug');
var utils = require('../../utils');
var log = debug('cli:files');
log.error = debug('cli:files:error');

module.exports = {
  command: 'cat <ipfs-path>',

  describe: 'Download IPFS objects',

  builder: {},

  handler: function handler(argv) {
    var path = argv['ipfs-path'];
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.files.cat(path, onFile);
    });
  }
};

function onFile(err, file) {
  if (err) {
    throw err;
  }
  file.pipe(process.stdout);
}